mapper.insert:
INSERT INTO MEMBER ( MEMBER_NO, MEMBER_ID, MEMBER_PWD, MEMBER_NAME, MEMBER_PHONE, MEMBER_AGE, MEMBER_EMAIL, MEMBER_ADDR, MEMBER_KEY, MEMBER_DATE
) VALUES( MEMBER_SEQ.NEXTVAL, #{member_id}, #{member_pwd}, #{member_name}, #{member_phone}, #{member_age}, #{member_email}, #{member_addr}, #{member_key}, sysdate
)

mapper.select:
SELECT GOODS_NUM,ORDER_DATE, MEMBER_ADDR, ORDER_PROCESS, ORDER_PAYMENT
FROM GOODS_PAYMENT
WHERE MEMBER_ID = #{member_id}
